insert into film (picture_id, status, country, director, fees,plot, release_year, title, rating)
values (5,'ACTIVE','Франция',' Оливье Накаш, Эрик Толедано','426588510',' Пострадав в результате несчастного случая, богатый аристократ Филипп нанимает в помощники человека, который менее всего подходит для этой работы, – молодого жителя предместья Дрисса, только что освободившегося из тюрьмы. Несмотря на то, что Филипп прикован к инвалидному креслу, Дриссу удается привнести в размеренную жизнь аристократа дух приключений.',2011,'1+1', '9.0');

