insert into film (picture_id, status, country, director, fees,plot, release_year, title)
values (1, 'ACTIVE', 'США', 'Фрэнк Дарабонт', '28000000', 'Бухгалтер Энди Дюфрейн обвинён в убийстве собственной жены и её любовника. Оказавшись в тюрьме под названием Шоушенк, он сталкивается с жестокостью и беззаконием, царящими по обе стороны решётки. Каждый, кто попадает в эти стены, становится их рабом до конца жизни. Но Энди, обладающий живым умом и доброй душой, находит подход как к заключённым, так и к охранникам, добиваясь их особого к себе расположения.',1994, 'Побег из Шоушенка');

insert into film (picture_id, status, country, director, fees,plot, release_year, title)
values (2,'ACTIVE','США','Фрэнк Дарабонт','286801374','Пол Эджкомб — начальник блока смертников в тюрьме «Холодная гора», каждый из узников которого однажды проходит «зеленую милю» по пути к месту казни. Пол повидал много заключённых и надзирателей за время работы. Однако гигант Джон Коффи, обвинённый в страшном преступлении, стал одним из самых необычных обитателей блока.',1999,'Зеленая миля');

insert into film (picture_id, status, country, director, fees,plot, release_year, title)
values (3,'ACTIVE','США','Роберт Земекис','677387716','Сидя на автобусной остановке, Форрест Гамп — не очень умный, но добрый и открытый парень — рассказывает случайным встречным историю своей необыкновенной жизни.С самого малолетства парень страдал от заболевания ног, соседские мальчишки дразнили его, но в один прекрасный день Форрест открыл в себе невероятные способности к бегу. Подруга детства Дженни всегда его поддерживала и защищала, но вскоре дороги их разошлись.',1994,'Форрест Гамп');

